export function CodingProfiles() {
  const containerRef = useRef<HTMLDivElement>(null);
  const headerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-in');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (headerRef.current) observer.observe(headerRef.current);
    const elements = containerRef.current?.querySelectorAll('[data-animate]');
    elements?.forEach((el) => observer.observe(el));

    return () => {
      if (headerRef.current) observer.unobserve(headerRef.current);
      elements?.forEach((el) => observer.unobserve(el));
    };
  }, []);

  return (
    <section id="coding-profiles" className="relative py-24 bg-background overflow-hidden">
      {/* Background animations */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/3 -left-32 w-72 h-72 bg-accent/10 rounded-full blur-3xl animate-pulse delay-500"></div>
        <div className="absolute bottom-1/4 -right-40 w-80 h-80 bg-secondary/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
      </div>

      <div ref={containerRef} className="relative z-10 max-w-6xl mx-auto px-6">
        {/* Section Header */}
        <div ref={headerRef} data-animate className="opacity-0 translate-y-10 transition-all duration-700 mb-16 text-center">
          <span className="text-accent font-medium text-sm uppercase tracking-wider">Competitive Coding</span>
          <h2 className="text-5xl md:text-6xl font-bold text-foreground mt-3 leading-tight">Coding Profiles</h2>
        </div>

        <div className="grid md:grid-cols-3 gap-8">
          {codingProfiles.map((profile, index) => (
            <div
              key={profile.id}
              data-animate
              className="opacity-0 translate-y-10 transition-all duration-700 group"
              style={{ transitionDelay: `${index * 100}ms` }}
            >
              <a
                href={profile.link}
                target="_blank"
                rel="noopener noreferrer"
                className={`relative h-56 block bg-slate-900/50 backdrop-blur-sm border ${profile.borderColor} rounded-xl p-8 transition-all duration-300 hover:shadow-2xl overflow-hidden group`}
              >
                <div className={`absolute inset-0 bg-gradient-to-br ${profile.hoverColor} opacity-0 group-hover:opacity-20 transition-opacity duration-300`}></div>

                {/* Border glow effect */}
                <div className={`absolute inset-0 border ${profile.borderColor} rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300 shadow-lg group-hover:shadow-xl`} style={{
                  boxShadow: `inset 0 0 20px ${profile.hoverColor.includes('cyan') ? 'rgba(34, 211, 238, 0.3)' : profile.hoverColor.includes('blue') ? 'rgba(59, 130, 246, 0.3)' : profile.hoverColor.includes('purple') ? 'rgba(139, 92, 246, 0.3)' : 'rgba(16, 185, 129, 0.3)'}`
                }}></div>

                {/* Content */}
                <div className="relative z-10 h-full flex flex-col items-center justify-center gap-6">
                  <div className="w-16 h-16 text-slate-300 group-hover:text-white transform group-hover:scale-110 transition-all duration-300">
                    {profile.logo}
                  </div>

                  {/* Platform name */}
                  <h3 className="text-2xl font-bold text-foreground group-hover:text-white transition-colors duration-300">
                    {profile.name}
                  </h3>

                  {/* External link icon */}
                  <ExternalLink className="text-slate-400 group-hover:text-white transition-all duration-300 transform group-hover:translate-y-1" size={20} />
                </div>
              </a>
            </div>
          ))}
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(40px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        [data-animate].animate-in {
          animation: fadeInUp 0.7s ease-out forwards;
        }

        .delay-500 {
          animation-delay: 500ms;
        }

        .delay-1000 {
          animation-delay: 1000ms;
        }
      `}</style>
    </section>
  );
}
